    //     ]
    //   })
    // }


  }
  render() {
    const {todoItems} = this.state;
    if (todoItems.length){
      return (
        <div className="App">
          {
            todoItems.length && todoItems.map((item, index) =>
            <TodoList
              key={index}
              item={item}
              onClick={this.onItemClicked} />
            )
          }
          <TrafficLight />
        </div>
      )
    }
    else{
      return(
        <div className="App">Nothing Here</div>
      )
    }
  }
}
